<script>

    $(function(){
        
        $('.mobile .button').on('click',function(){
            $('#menu').toggleClass('view');
            if(!$('#menu').hasClass('view')){
                $('.mobile .menubox ul li').removeClass('view');
            }
        })
        $("body").on("click",function(e){
            if($("#menu").hasClass("view")){
                if(!$("#menu").has(e.target).length){
                    $("#menu").removeClass("view");
                    $('.mobile .menubox ul li').removeClass('view');
                }
            }
        })
        
        $('.mobile .menubox ul li').on('click',function(){
            if($(this).hasClass('view')){
                $('.mobile .menubox ul li').removeClass('view');
                $(this).removeClass('view');
            }else{
                $('.mobile .menubox ul li').removeClass('view');
                $(this).addClass('view');
            }
        })
    })

</script>
